mapper.select:
select menu_id
from t_sys_pm_role_menu
where role_id = #{roleId,jdbcType=BIGINT}
and state = #{state,jdbcType=SMALLINT}

update.foreach:
update t_sys_pm_role_menu
role_id = #{item.roleId,jdbcType=BIGINT}, menu_id = #{item.menuId,jdbcType=BIGINT}, state = #{item.state,jdbcType=SMALLINT}, create_user_id = #{item.createUserId,jdbcType=BIGINT}, create_user_name = #{item.createUserName,jdbcType=VARCHAR}, create_date = #{item.createDate,jdbcType=TIMESTAMP}, modify_user_id = #{item.modifyUserId,jdbcType=BIGINT}, modify_user_name = #{item.modifyUserName,jdbcType=VARCHAR}, modify_date = #{item.modifyDate,jdbcType=TIMESTAMP}
where role_menu_id = #{item.roleMenuId,jdbcType=BIGINT}
